#!/usr/bin/perl

use strict;
use warnings;

my $str = $ARGV[0];
my $shift = $ARGV[1];

print shift_string($str, $shift);

sub shift_string {
    my ($str, $shift) = @_;
    $str =~ s/([a-z])/$1 eq 'z' ? 'a' : chr(ord($1) + $shift)/eg;
    $str =~ s/([A-Z])/$1 eq 'Z' ? 'A' : chr(ord($1) + $shift)/eg;
    return $str;
}
