def find_mode(num_list):
    if not num_list:
        raise ValueError("Input list is empty")

    frequency_dict = {}
    for num in num_list:
        if num in frequency_dict:
            frequency_dict[num] += 1
        else:
            frequency_dict[num] = 1

    max_frequency = max(frequency_dict.values())
