#include <iostream>
#include <vector>

bool isSuperset(std::vector<int> arr1, std::vector<int> arr2) {
    for (int i = 0; i < arr2.size(); i++) {
        bool flag = false;
        for (int j = 0; j < arr1.size(); j++) {
            if (arr1[j] == arr2[i]) {
                flag = true;
                break;
            }
        }
        if (!flag) {
            return false;
        }
    }
    return true;
}

