#include <iostream>
#include <string>

using namespace std;

string toLower(string s) {
    for (int i = 0; i < s.length(); i++) {
        if (s[i] >= 'A' && s[i] <= 'Z') {
            s[i] = s[i] + 32;
        }
    }
    return s;
}

string toUpper(string s) {
    for (int i = 0; i < s.length(); i++) {
        if (s[i] >= 'a' && s[i] <= 'z') {
            s[i] = s[i] - 32;
        }
    }
    return s;
}

int main() {
    string s;
    cin >> s;

    string lower = toLower(s);
    string upper = toUpper(s);

    cout << lower << endl;
    cout << upper << endl;

    return 0;
}
