#include <iostream>
#include <map>
#include <string>

int main() {
    // Create a dictionary
    std::map<std::string, std::string> dict = {{"key1", "value1"}, {"key2", "value2"}};

    // Access a value using a variable
    std::string key = "key1";
    std::cout << dict[key] << std::endl;  // Output: value1

    // Manipulate a value using a variable
    dict[key] = "new_value";
    std::cout << dict[key] << std::endl;  // Output: new_value

