#include <iostream>
#include <vector>

int findIndex(const std::vector<int>& arr, int val) {
    for (auto it = arr.begin(); it != arr.end(); ++it) {
        if (*it == val) {
            return std::distance(arr.begin(), it);
        }
    }
    return -1;
}

