def find_highest_average_grade(students):
    highest_average_grade = 0
    highest_average_grade_students = []

    for student, grades in students.items():
        average_grade = sum(grades.values()) / len(grades)
        if average_grade > highest_average_grade:
            highest_average_grade = average_grade
            highest_average_grade_students = [student]
        elif average_grade == highest_average_grade:
            highest_average_grade_students.append(student)

    return highest_average_grade_students

