#include <stdio.h>
#include <stdlib.h>

void swap(int* a, int* b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

void reverseArray(int* a, int n) {
    int* left = a;
    int* right = a + n - 1;

    while (left < right) {
        swap(left, right);
        left++;
        right--;
    }
}

void printArray(int* a, int n) {
    for (int i = 0; i < n; i++) {
        printf("%d ", a[i]);
    }
    printf("\n");
}

int main() {
    int a[] = {1, 2, 3, 4, 5};
    int n = sizeof(a) / sizeof(a[0]);

    printf("Original array: ");
    printArray(a, n);

    reverseArray(a, n);

    printf("Reversed array: ");
    printArray(a, n);

    return 0;
}
```
