#include <stdio.h>

int main() {
    int i, n, j;
    int space;

    printf("Enter the number of rows: ");
    scanf("%d", &n);
    space = n - 1;

    for (i = 1; i <= n; i++) {
        for (j = 1; j <= space; j++) {
            printf(" ");
        }
        for (j = 1; j <= i; j++) {
            printf("*");
        }
        printf("\n");
        space--;
    }

    return 0;
}
```
