import math

# Constants
pi = math.pi

# Functions
def add(x, y):
    return float(x) + float(y)

def sub(x, y):
    return float(x) - float(y)

def mul(x, y):
    return float(x) * float(y)

def div(x, y):
    if y != 0:
        return float(x) / float(y)
    else:
        return "Error: Division by zero is not allowed"

# Main Program
if __name__ == "__main__":
    x = input("Enter the first number: ")
    y = input("Enter the second number: ")

    print("Choose an operation:")
