#include <iostream>
#include <string>

using namespace std;

int main() {
    string palavra;
    cout << "Escreva uma palavra: ";
    cin >> palavra;

    int i = 0;
    int j = palavra.size() - 1;

    int k = 0;

    for (i = 0; i < palavra.size(); i++) {
        if (palavra[i] == palavra[palavra.size() - i - 1]) {
            k++;
        }
    }

    if (k == palavra.size()) {
        cout << "Essa palavra é um palindromo." << endl;
    } else {
        for (j = palavra.size() - 1; j >= 0; j--) {
            cout << palavra[j];
        }
        cout << endl;
    }

